sys_variable_value.value:
(function parse(inputs, outputs) {

// Source item string is available in inputs.sourceItem.
// Generate output by setting fields of outputs.targetObject.
// 
// The structure of outputs.targetObject corresponds to the
// structure defined in the Data Stream Action output.

// Parse glacier record properties into the outputs.targetObject
// that is passed to the Data Stream Action Outputs.
 var glacier_rec = JSON.parse(inputs.sourceItem);
 outputs.targetObject.id        = glacier_rec.glacier_id;
 outputs.targetObject.name      = glacier_rec.name;
 outputs.targetObject.country   = glacier_rec.country;
 outputs.targetObject.latitude  = glacier_rec.latitude;
 outputs.targetObject.longitude = glacier_rec.longitude;



})(inputs, outputs);

(function execute(inputs, outputs) {

  // If the ordinal number of pages to get (inputs.first_glacier) value   
  // is zero or less than zero, or if the ordinal number is greater than 
  // the number of glacier records in the database, set 
  // outputs.first_glacer_to_get to the string value 1.  If not, set the 
  // value of first_glacer_to_get to inputs.first_glacier.
  if (parseInt(inputs.first_glacier) <= 0 || parseInt(inputs.first_glacier) > 132890) {
    outputs.first_glacier_to_get = "1";
  } else {
    outputs.first_glacier_to_get = inputs.first_glacier;
  }

  // The World Glaciers web service returns a 442 error if the page 
  // size is not between 1 and 10 inclusive. To avoid receiving an 
  // error, check the value of the page size (inputs.glaciers_per_page).  
  // If the value is outside of the allowed range, set 
  // outputs.glaciers_per_page to the string value 1.  Otherwise, 
  // set outputs.glaciers_per_page to inputs.glaciers_per_page.

  if (parseInt(inputs.glaciers_per_page) <= 0 || parseInt(inputs.glaciers_per_page) > 10) {
    outputs.glaciers_per_page = "1";
  } else {
    outputs.glaciers_per_page = inputs.glaciers_per_page;
  }


  // If the number of pages to get (inputs.pages_to_get) value is zero   
  // or less than zero, set outputs.number_of_pages to the string 
  // value 1. If not, set the value of outputs.number_of_pages to 
  // inputs.pages_to_get.
  if (parseInt(inputs.pages_to_get) <= 0) {
    outputs.number_of_pages = "1";
  } else {
    outputs.number_of_pages = inputs.pages_to_get;
  }
})(inputs, outputs);

